import React, { Component } from 'react';
import NavBarView from '../Views/NavBarView';
import { ProductConsumer } from '../Data/Context';
import ProductView from '../Views/ProductView';



export default class ProductListingPage extends Component {
    selectMapper = (filters) => {
        return (
            filters.map((filter) => {
                return (
                    filter.type === 'BRAND' ?

                        filter.values.map((value) => <option key={value.value}>{value.value}</option>) : null
                )

            })
        )
    }
    ColorSelectMapper = (filters) => {
        return (
            filters.map((filter) => {
                return (
                    filter.type === 'COLOUR' ?

                        filter.values.map((value) => <option key={value.color}>{value.title}</option>) : null
                )

            })
        )
    }
    maxPriceSelectMapper = (filters) => {
        return (
            filters.map((filter) => {
                return (
                    filter.type === 'PRICE' ?

                        filter.values.map((value) => {
                            return (
                                value.key > 2000 || value.key === "Max" ?
                                    <option key={value.key}>{value.displayValue}</option> : null)
                        }) : null
                )

            })
        )
    }
    minpriceSelectMapper = (filters) => {
        return (
            filters.map((filter) => {
                return (
                    filter.type === 'PRICE' ?

                        filter.values.map((value) => {
                            return (
                                value.key < 2000 || value.key === "Min" ?
                                    <option key={value.key}>{value.displayValue}</option> : null)
                        }) : null
                )

            })
        )
    }

    render() {
        return (
            <ProductConsumer>
                {
                    value => {
                        return (
                            <React.Fragment>
                                <NavBarView searchOnchangeHandler={value.searchOnchangeHandler} />
                                <div className="bottom-container">
                                    <div className="bottom-container-left">
                                        <div className="price-filter-wrapper">
                                            <select className="brand-select" onChange={(e) => value.SelectOnChangeHandler({ brandFilterValue: e.target.value })}>
                                                <option value="none"> --Select Brand--</option>
                                                {this.selectMapper(value.filters)}
                                            </select>
                                            <select className="color-select" >
                                                <option value="none"> --Select Colour--</option>
                                                {this.ColorSelectMapper(value.filters)}
                                            </select>
                                            <select>
                                                <option value="none"> --Select Min Price--</option>
                                                {this.minpriceSelectMapper(value.filters)}
                                            </select>
                                            <select>
                                                <option value="none"> --Select Max Price--</option>
                                                {this.maxPriceSelectMapper(value.filters)}
                                            </select>
                                            <div className="filter-submit-button-wrapper">
                                                <button className="filter-submit-button" onClick={value.filterSubmitHandler} >Filter</button>
                                            </div>
                                        </div>

                                    </div>


                                    <div className="bottom-container-right">
                                        <ProductConsumer>
                                            {value => {
                                                return (
                                                    value.searchString ?
                                                        value.productData.filter((product) => product.brand.includes(value.searchString.toLowerCase()))
                                                            .map(product => { return <ProductView key={product.id} product={product} /> }) :
                                                        value.productData.map(product => { return <ProductView key={product.id} product={product} /> })

                                                )
                                            }}
                                        </ProductConsumer>



                                    </div>
                                </div>
                            </React.Fragment>
                        )
                    }
                }

            </ProductConsumer>
        )
    }
}
