import React, { Component } from 'react';
import LoginPages from '../Views/LoginView';
import { ProductConsumer } from '../Data/Context';
import axios from 'axios';

export default class LoginPage extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: null,
            password: null,
            users: null,
            isValid: true
        }
    }

    componentDidMount() {
        axios.get("https://xebiascart.herokuapp.com/users")
            .then((response) => {
                this.setState({ users: response.data })
            })
    }

    onInputChange = (payload) => {
        this.setState({ ...this.state, ...payload });
    }
    onSubmitHandler = (e) => {
        e.preventDefault();
        const validUser = this.state.users.filter(user => user.username === this.state.username && user.password === this.state.password);

        if (validUser.length !== 0) {
            this.setState({ isValid: true }, this.redirectTicketList)
        } else {
            this.setState({ isValid: false })
        }

    }

    redirectTicketList = () => {
        // const token = window.localStorage.getItem('_token');
        // if (token != null) {
        //     this.props.history.push('/ticketlist');
        // }
        if (this.state.isValid === true) {
            this.props.history.push('/home')
        }
    }

    render() {
        return (
            <LoginPages {...{
                ...this.state,
                onInputChange: this.onInputChange,
                onSubmitHandler: this.onSubmitHandler
            }} />
        );
    }

}
